/*select * from dba_data_files*//*drop user libysys cascade */create tablespace libysysdatafile     'd:\dat\libysys.dat'    size 2m    autoextend on        next 1m        maxsize unlimited     online     permanent    extent management        local            autoallocate;create user libysys identified by libysys  default tablespace libysys  temporary tablespace temp;grant connect to libysys;grant resource to libysys;grant dba to libysys;/*触发器建表的权限*/grant create table to libysys;--===============================================================--select * from dba_data_files--创建临时表空间create temporary tablespace tbs_libysys_temp  tempfile 'D:\DAT\libysys\libysys_temp.dbf' size 2m  autoextend on  next 1m maxsize 1024m  extent management local;  --创建数据表空间create tablespace tbs_libysys  datafile 'D:\DAT\libysys\libysys.dbf' size 2m  autoextend on  next 1m maxsize 1024m  extent management local;  --创建索引表空间create tablespace tbs_libysys_index  datafile 'D:\DAT\libysys\libysys_index.dbf' size 2m  autoextend on  next 1m maxsize 1024m  extent management local; --创建用户,指定表空间create user  libysys identified  by libysysdefault tablespace tbs_libysys  temporary tablespace tbs_libysys_temp;  --赋与用户普通DBA权限grant dba to libysys;grant connect to libysys;grant resource to libysys;/*触发器建表的权限*/--grant create table to libysys;